mapper.select:
select

from member_channel_deduct
where 1=1

	and name like concat
	('%',#{name},'%')


	AND memberID like concat
	('%',#{memberID},'%')



		AND last_login_from = #{lastLoginFrom}


		AND last_login_from is NULL



	AND register_date > #{registerDate}


	AND register_date  < #{endDate}

order by id desc
limit #{begin},#{pageSize}

select count(1) from member_channel_deduct where 1=1
AND name like concat ('%',#{name},'%')
AND memberId like concat ('%',#{memberid},'%')
AND last_login_from = #{lastLoginFrom}
AND last_login_from is NULL
AND register_date > #{registerDate}
AND register_date < #{endDate}
